uint16_t get_tapping_term(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        case LGUI_T(KC_A):
            return TAPPING_TERM + 80;
        case LALT_T(KC_S):
            return TAPPING_TERM + 80;
        case LCTL_T(KC_D):
            return TAPPING_TERM + 50;
        default:
            return TAPPING_TERM;
    }
}


